INSERT INTO [dbo].[KnowledgeAreas] ([Id], [CreatedAt], [UpdatedAt], [Deleted], [KnowledgeAreaNumber], [KnowledgeAreaText], [KnowledgeAreaDescription], [PmbokVersionId]) VALUES (N'9ae8ea103c8148339131997358e92ca9', N'9/25/2018 8:20:57 PM +00:00', N'9/25/2018 8:20:57 PM +00:00', 0, 4, N'Project Integration Management', N'Project Integration Management includes the processes and activities needed to identify, define, combine, unify and coordinate the various processes and project management activities within the Project Management Process Groups.', N'edd7dca4d2a94c7a8d6f55fbef403f8c')
INSERT INTO [dbo].[KnowledgeAreas] ([Id], [CreatedAt], [UpdatedAt], [Deleted], [KnowledgeAreaNumber], [KnowledgeAreaText], [KnowledgeAreaDescription], [PmbokVersionId]) VALUES (N'c03843f7d5c241fbbee703626450e2b5', N'9/25/2018 8:20:58 PM +00:00', N'9/25/2018 8:20:58 PM +00:00', 0, 5, N'Project Scope Management', N'Project Scope Management includes the processes required to ensure that the project includes all the work required and only the work required, to complete the project successfully.', N'edd7dca4d2a94c7a8d6f55fbef403f8c')
INSERT INTO [dbo].[KnowledgeAreas] ([Id], [CreatedAt], [UpdatedAt], [Deleted], [KnowledgeAreaNumber], [KnowledgeAreaText], [KnowledgeAreaDescription], [PmbokVersionId]) VALUES (N'28abfe5102fa474e9c5fb8905d53c5cf', N'9/25/2018 8:20:58 PM +00:00', N'9/25/2018 8:20:58 PM +00:00', 0, 6, N'Project Time Management', N'Project Time Management includes the processes required to manage timely completion of the project.', N'edd7dca4d2a94c7a8d6f55fbef403f8c')
INSERT INTO [dbo].[KnowledgeAreas] ([Id], [CreatedAt], [UpdatedAt], [Deleted], [KnowledgeAreaNumber], [KnowledgeAreaText], [KnowledgeAreaDescription], [PmbokVersionId]) VALUES (N'58a094a415d94e4e9930a1fd871ab198', N'9/25/2018 8:20:59 PM +00:00', N'9/25/2018 8:20:59 PM +00:00', 0, 7, N'Project Cost Management', N'Project Cost Management includes the processes involved in estimating, budgeting and controlling costs so that the project can be completed within the approved budget.', N'edd7dca4d2a94c7a8d6f55fbef403f8c')
INSERT INTO [dbo].[KnowledgeAreas] ([Id], [CreatedAt], [UpdatedAt], [Deleted], [KnowledgeAreaNumber], [KnowledgeAreaText], [KnowledgeAreaDescription], [PmbokVersionId]) VALUES (N'e669ee7ec78f4f57acb525de3c48f250', N'9/25/2018 8:20:59 PM +00:00', N'9/25/2018 8:20:59 PM +00:00', 0, 8, N'Project Quality Management', N'Project Quality Management includes the processes and activities of the performing organization that determine quality policies, objectives and responsibilities so that the project will satisfy the needs for which it was undertaken.', N'edd7dca4d2a94c7a8d6f55fbef403f8c')
INSERT INTO [dbo].[KnowledgeAreas] ([Id], [CreatedAt], [UpdatedAt], [Deleted], [KnowledgeAreaNumber], [KnowledgeAreaText], [KnowledgeAreaDescription], [PmbokVersionId]) VALUES (N'f12501ebfa6a4fc08e697e06513a92f4', N'9/25/2018 8:21:00 PM +00:00', N'9/25/2018 8:21:00 PM +00:00', 0, 9, N'Project Human Resource Management', N'Project Human Resource Management includes the processes that organize, manage and lead the project team.', N'edd7dca4d2a94c7a8d6f55fbef403f8c')
INSERT INTO [dbo].[KnowledgeAreas] ([Id], [CreatedAt], [UpdatedAt], [Deleted], [KnowledgeAreaNumber], [KnowledgeAreaText], [KnowledgeAreaDescription], [PmbokVersionId]) VALUES (N'f27ea71961544e6b815dedcd0d766c20', N'9/25/2018 8:21:00 PM +00:00', N'9/25/2018 8:21:00 PM +00:00', 0, 10, N'Project Communication Management', N'Project Communication Management includes the processes required to ensure timely and appropriate generation, collection, distribution, storage, retrieval and ultimate disposition of project information.', N'edd7dca4d2a94c7a8d6f55fbef403f8c')
INSERT INTO [dbo].[KnowledgeAreas] ([Id], [CreatedAt], [UpdatedAt], [Deleted], [KnowledgeAreaNumber], [KnowledgeAreaText], [KnowledgeAreaDescription], [PmbokVersionId]) VALUES (N'a1d7b3a645314bf7b16357f69c74d5d8', N'9/25/2018 8:21:00 PM +00:00', N'9/25/2018 8:21:00 PM +00:00', 0, 11, N'Project Risk Management', N'Project Risk Management includes the processes of conducting risk management planning, identification, analysis, response planning and monitoring and control of the project.', N'edd7dca4d2a94c7a8d6f55fbef403f8c')
INSERT INTO [dbo].[KnowledgeAreas] ([Id], [CreatedAt], [UpdatedAt], [Deleted], [KnowledgeAreaNumber], [KnowledgeAreaText], [KnowledgeAreaDescription], [PmbokVersionId]) VALUES (N'bf2e3d53cf064ab6a3c3cfa7f164c236', N'9/25/2018 8:21:01 PM +00:00', N'9/25/2018 8:21:01 PM +00:00', 0, 12, N'Project Procurement Management', N'Project Procurement Management includes the processes necessary to purchase or acquire products, services or results from outside the project team. ', N'edd7dca4d2a94c7a8d6f55fbef403f8c')
